import React from "react";
import { useState } from "react";
import "./toogle.css";
const Toggle = () => {
  const [show, setShow] = useState(true);
  return (
    <div className="parent">
      <nav>
        <div className="left-link">
          <a href="">Sing up</a>
          <a href="">Login</a>
          <div className="toggle">
          <button className="toggle-btn" onClick={()=>setShow(!show)}>=</button>
        </div>
        </div>
        {show ? 
         <div style={{transition: 'all 6s linear'}} className="right-link">
         <a href="">About us</a>
         <a href="">Contact</a> 
         <a href="">Contact</a>
         <a href="">Contact</a>
       </div>
         : "" }
      </nav>
      <div>
        <h4> whta you know abiut me</h4>
        <p>I;m your friend</p>
        okk I know you are
      </div>
    </div>
  );
};

export default Toggle;
